import React, { Component } from "react";
import "../sidebar/sidebarStyle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStickyNote } from "@fortawesome/free-solid-svg-icons";

class Notes extends Component {
  state = {
    options: [
      {
        option: "View All",
        func: this.props.loadNotes,
        isSolid: true
      },
      { option: "Write Note", id: "001", func: this.props.viewProject }
    ]
  };
  render() {
    const openDivStyle = {
      width: "150px",
      paddingLeft: "20px",
      float: "left",
      overflow: "hidden"
    };
    const closeDivStyle = {
      width: "0px",
      display: "none"
    };
    const openIconStyle = {
      float: "left",
      marginLeft: "5%"
    };
    const closeIconStyle = {
      textAlign: "center"
    };
    return (
      <React.Fragment>
        <div
          className="selectionClose"
          style={this.props.isExpanded ? openIconStyle : closeIconStyle}
        >
          <FontAwesomeIcon
            icon={faStickyNote}
            size="lg"
            onClick={this.props.expandNav}
          />
        </div>
        <div style={this.props.isExpanded ? openDivStyle : closeDivStyle}>
          {this.state.options.map(x => (
            <p
              key={x.id}
              className="expandOption"
              dataValue={x.id}
              dataName={x.option}
              onClick={x.isSolid ? x.func : this.props.viewProject}
            >
              {x.option}
            </p>
          ))}
        </div>
      </React.Fragment>
    );
  }
}

export default Notes;
