import React, { Component } from "react";
import "./weather.css";
// import { _loadWeather } from "./weatherService";
import { _loadCurrentWeather } from "./weatherService";
import Temperature from "./temperature";
import Indicator from "./weatherIndicator";

class Weather extends Component {
  state = { weather: " " };

  componentDidMount() {
    return _loadCurrentWeather().then(resultingJSON =>
      this.setState({ weather: resultingJSON })
    );
  }

  render() {
    return (
      <div className="weatherBox">
        {/* <button onClick={this.testMap}>test</button> */}
        <Indicator description={this.state.weather.description} />
        <Temperature weather={this.state.weather} />
        {/* <TimeClock /> */}
        <span className="fadeIn">{this.props.time}</span>
      </div>
    );
  }
}

export default Weather;
