import React, { Component } from "react";
import "../sidebar/sidebarStyle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faProjectDiagram } from "@fortawesome/free-solid-svg-icons";

class Projects extends Component {
  state = {
    isOpen: false,
    options: [
      {
        option: "View All",
        id: "000",
        func: this.props.handleProjects,
        isSolid: true
      },
      { option: "Project 1", id: "001", func: this.props.viewProject },
      { option: "Project 2", id: "002", func: this.props.viewProject },
      { option: "Project 3", id: "003", func: this.props.viewProject }
    ]
  };

  //need a componentdidmount once mongo is connected. all options except view all will be database saved. use 'id' key to store mongo ._id to send request to db

  render() {
    const openDivStyle = {
      width: "150px",
      paddingLeft: "20px",
      float: "left",
      overflow: "hidden"
    };
    const closeDivStyle = {
      width: "0px",
      display: "none"
    };
    const openIconStyle = {
      float: "left",
      marginLeft: "5%"
    };
    const closeIconStyle = {
      textAlign: "center"
    };
    return (
      <React.Fragment>
        <div style={this.props.isExpanded ? openDivStyle : closeDivStyle}>
          {this.state.options.map(x => (
            <p
              key={x.id}
              datavalue={x.id}
              dataname={x.option}
              onClick={
                x.isSolid ? this.props.handleProjects : this.props.viewProject
              }
            >
              {x.option}
            </p>
          ))}
        </div>
      </React.Fragment>
    );
  }
}

export default Projects;
