import React, { Component } from "react";
import "../sidebar/sidebarStyle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faProjectDiagram } from "@fortawesome/free-solid-svg-icons";

class Projects extends Component {
  state = {
    expanded: false,
    options: [
      {
        option: "View All",
        func: this.props.handleProjects,
        isSolid: true
      },
      { option: "Project 1", id: "001", func: this.props.viewProject },
      { option: "Project 2", id: "002", func: this.props.viewProject },
      { option: "Project 3", id: "003", func: this.props.viewProject }
    ]
  };

  //need a componentdidmount once mongo is connected. all options except view all will be database saved

  render() {
    const divStyle = {
      backgroundColor: "blue"
    };
    return (
      <div className="selectionClose">
        <FontAwesomeIcon
          icon={faProjectDiagram}
          size="lg"
          onClick={this.props.expand}
        />
      </div>

