import React, { Component } from "react";
import "../sidebar/sidebarStyle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faProjectDiagram } from "@fortawesome/free-solid-svg-icons";

class Projects extends Component {
  state = {
    isExpanded: false,
    options: [
      {
        option: "View All",
        id: "000",
        func: this.props.handleProjects,
        isSolid: true
      },
      { option: "Project 1", id: "001", func: this.props.viewProject },
      { option: "Project 2", id: "002", func: this.props.viewProject },
      { option: "Project 3", id: "003", func: this.props.viewProject }
    ]
  };

  //   window.addEventListener('click', function(event){
  //     var box = document.getElementById('box1');
  //     if (event.target != box && event.target.parentNode != box){
  //         box.style.display = 'none';
  //     }
  // });

  //need a componentdidmount once mongo is connected. all options except view all will be database saved. use 'id' key to store mongo ._id to send request to db
  componentDidMount() {
    document.addEventListener("mousedown", this.handleClickOut);
  }
  componentDidMount() {
    document.removeEventListener("mousedown", this.handleClickOut);
  }
  handleClickOut = e => {
    this.setState({ isExpanded: false });
  };

  expand = e => {
    if (this.node.contains(e.target)) {
      if (this.state.isExpanded === false) this.setState({ isExpanded: true });
      else this.setState({ isExpanded: false });
    }
    this.handleClickOut();
  };

  render() {
    const openDivStyle = {
      width: "100px",
      float: "left",
      overflow: "hidden"
    };
    const closeDivStyle = {
      width: "0px",
      display: "none"
    };
    return (
      <React.Fragment>
        <div className="option">
          <FontAwesomeIcon
            className="optionIcon"
            icon={faProjectDiagram}
            size="lg"
            onClick={this.expand}
            ref={node => (this.node = node)}
          />
          <div
            className="expandOptions"
            style={this.state.isExpanded ? openDivStyle : closeDivStyle}
          >
            {this.state.options.map(x => (
              <p
                className="expandOption"
                key={x.id}
                datavalue={x.id}
                dataname={x.option}
                onClick={
                  x.isSolid ? this.props.handleProjects : this.props.viewProject
                }
                onMouseUp={() => this.setState({ isExpanded: false })}
              >
                {x.option}
              </p>
            ))}
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Projects;
