import React, { Component } from "react";
import Projects from "../tools/projects";
class Nav extends Component {
  state = { isExpanded: false };
  expand = e => {
    if (this.state.isExpanded === false) this.setState({ isExpanded: true });
    else this.setState({ isExpanded: false });
  };
  render() {
    const openDivStyle = {
      width: "300px",
      backgroundColor: "green",
      paddingLeft: "80px"
    };
    const closeDivStyle = {
      width: "0px",
      dispay: "none"
    };
    return (
      <React.Fragment>
        <div className="sideNav">
          <Projects expand={this.expand} />
        </div>
        <div style={this.state.isExpanded ? openDivStyle : closeDivStyle}>
          hello
        </div>
      </React.Fragment>
    );
  }
}

export default Nav;
