import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHamburger, faCalendar } from "@fortawesome/free-solid-svg-icons";
import "./sidebarStyle.css";
class Sidebar extends Component {
  state = {
    openClose: "close"
  };
  collapse = () => {
    if (this.state.openClose == "close") this.setState({ openClose: "open" });
    else this.setState({ openClose: "close" });
  };

  render() {
    const pStyle = {
      paddingLeft: "20px"
    };

    return (
      <div>
        {this.state.openClose == "close" && (
          <div className="sideNavClose">
            <p className="burger" onClick={this.collapse}>
              <FontAwesomeIcon icon={faHamburger} size="lg" />
            </p>
            <p className="selection" onClick={this.props.handleCalendar}>
              <FontAwesomeIcon icon={faCalendar} size="lg" />
            </p>
          </div>
        )}
        {this.state.openClose == "open" && (
          <div className="sideNavOpen">
            <p className="burger" style={pStyle}>
              <FontAwesomeIcon
                icon={faHamburger}
                size="lg"
                onClick={this.collapse}
              />
            </p>
            <p
              className="selection"
              onClick={this.props.handleCalendar}
              style={pStyle}
            >
              <FontAwesomeIcon icon={faCalendar} size="lg" />
              Calendar
            </p>
          </div>
        )}
      </div>
    );
  }
}

export default Sidebar;
