import React, { Component } from "react";
import Projects from "../tools/projects";
import Notes from "../tools/notes";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faProjectDiagram,
  faStickyNote
} from "@fortawesome/free-solid-svg-icons";
class Nav extends Component {
  state = {
    isExpanded: false
  };
  expand = e => {
    if (this.state.isExpanded === false) this.setState({ isExpanded: true });
    else this.setState({ isExpanded: false });
  };
  render() {
    const openNavStyle = {
      width: "200px",
      textAlign: "initial"
    };
    const closeNavStyle = {
      width: "50px"
    };
    return (
      <React.Fragment>
        <div
          className="sideNav"
          //   style={this.props.isExpanded ? openNavStyle : closeNavStyle}
        >
          <div className="option">
            <FontAwesomeIcon
              className="optionIcon"
              icon={faProjectDiagram}
              size="lg"
              onClick={this.expand}
            />
            <div className="expandOptions">
              <Projects isExpanded={this.state.isExpanded} />
            </div>
          </div>
          <div className="option">
            <FontAwesomeIcon
              className="optionIcon"
              icon={faStickyNote}
              size="lg"
              onClick={this.expand}
            />
            <div className="expandOptions">
              <Notes isExpanded={this.state.isExpanded} />
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Nav;
