import React, { Component } from "react";
import "../sidebar/sidebarStyle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faStickyNote,
  faChevronDown,
  faSearch
} from "@fortawesome/free-solid-svg-icons";

class Notes extends Component {
  state = {
    isExpanded: false,
    isActive: false,
    isQuickSelect: false,
    search: null,
    options: [
      {
        option: "View All",
        id: "000",
        func: this.props.loadNotes,
        isSolid: true
      },
      { option: "Write Note", id: "001", func: this.props.viewProject }
    ],
    quickNote: {
      text: null,
      date: "today"
      //get date once handleSubmit is clicked before posting to db
    }
  };

  componentDidMount() {
    document.addEventListener("mousedown", this.expand, false);
  }
  componentWillUnmount() {
    document.removeEventListener("mousedown", this.expand, false);
  }
  handleClickOut = e => {
    this.setState({ isExpanded: false });
    this.setState({ isActive: false });
  };

  handleViewNotes = e => {
    this.props.loadNotes(e);
    this.setState({ isExpanded: false });
    this.setState({ isActive: false });
  };

  handleWriteNote = e => {
    this.setState({ isQuickSelect: true });
    this.setState({ isExpanded: false });
  };

  expand = e => {
    if (this.node.contains(e.target)) {
      if (this.state.isExpanded === false) {
        this.setState({ isExpanded: true });
        this.setState({ isActive: true });
        this.setState({ isQuickSelect: false });
      }
      return;
    } else this.handleClickOut();
  };

  handleChange = e => {
    let value = e.target.value;
    this.setState({ quickNote: { text: value } });
  };
  handleSearchInput = e => {
    let value = e.target.value;
    this.setState({ search: value });
  };
  handleSubmit = () => {
    if (this.state.quickNote.text !== null) {
      console.log(this.state.quickNote);
    } else alert("write something!");
  };
  handleCancel = () => {
    this.setState({ quickNote: { text: null } });
    this.setState({ isQuickSelect: false });
  };

  render() {
    const openDivStyle = {
      width: "120px",
      float: "left",
      backgroundColor: "lightgreen",
      overflow: "hidden"
    };
    const closeDivStyle = {
      width: "0px",
      display: "none"
    };
    const activeDivStyle = {
      backgroundColor: "lightgreen"
    };
    const inactiveDivStyle = {
      backgroundColor: "lightsalmon"
    };
    const inputStyle = {
      position: "relative",
      top: "160px"
    };
    return (
      <React.Fragment>
        <div
          className="option"
          ref={node => (this.node = node)}
          onClick={this.expand}
          style={this.state.isActive ? activeDivStyle : inactiveDivStyle}
        >
          <FontAwesomeIcon
            className="optionIcon"
            icon={faStickyNote}
            size="lg"
          />
          <div
            className="expandOptions"
            style={this.state.isExpanded ? openDivStyle : closeDivStyle}
          >
            {this.state.options.map(x => (
              <p
                key={x.id}
                className="expandOption"
                datavalue={x.id}
                dataname={x.option}
                onClick={
                  x.isSolid ? this.handleViewNotes : this.handleWriteNote
                }
              >
                {x.option}
              </p>
            ))}
          </div>
        </div>
        {this.state.isQuickSelect === true && (
          <div className="quickSelect">
            <h4>Quick Note</h4>
            <textarea
              rows="4"
              cols="30"
              placeholder="write note"
              onChange={this.handleChange}
            ></textarea>
            <div>
              <p>Add to Project</p>
            </div>
            <input
              type="text"
              placeholder="search..."
              onChange={this.handleSearchInput}
            />
            <button onClick={this.handleSearchOne}>
              <FontAwesomeIcon icon={faSearch} />
            </button>
            <button onClick={this.handleSearchAll}>
              <FontAwesomeIcon icon={faChevronDown} />
            </button>
            <div>
              <button onClick={this.handleCancel}>Cancel</button>
              <button onClick={this.handleSubmit}>Submit</button>
            </div>
          </div>
        )}
      </React.Fragment>
    );
  }
}

export default Notes;
