import React, { Component } from "react";
import "../sidebar/sidebarStyle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faStickyNote,
  faChevronDown,
  faSearch
} from "@fortawesome/free-solid-svg-icons";

class CreateNote extends Component {
  state = {};

  componentDidMount() {
    document.addEventListener("mousedown", this.expand, false);
  }
  componentWillUnmount() {
    document.removeEventListener("mousedown", this.expand, false);
  }
  //rework this func to minimize when clicked out.
  expand = e => {
    if (this.node.contains(e.target)) {
      if (this.state.isExpanded === false) {
        this.setState({ isExpanded: true });
        this.setState({ isActive: true });
        this.setState({ isQuickSelect: false });
      }
      return;
    } else this.handleClickOut();
  };

  render() {
    return (
      <React.Fragment>
        <div className="quickSelect" ref={node => (this.node = node)}>
          <h4>Add Note</h4>
          <textarea
            rows="4"
            cols="30"
            placeholder="write note"
            onChange={this.props.handleChange}
          ></textarea>
          <div>
            <p>Add to Project</p>
          </div>
          <input
            type="text"
            placeholder="search..."
            onChange={this.props.handleSearchInput}
          />
          <button onClick={this.props.handleSearchOne}>
            <FontAwesomeIcon icon={faSearch} />
          </button>
          <button onClick={this.props.handleSearchAll}>
            <FontAwesomeIcon icon={faChevronDown} />
          </button>
          <div>
            <button onClick={this.props.handleCancel}>Cancel</button>
            <button onClick={this.props.handleSubmit}>Submit</button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default CreateNote;
