import React, { Component } from "react";
import Projects from "../tools/projects";
class Nav extends Component {
  state = { isExpanded: false };
  expand = e => {
    if (this.state.isExpanded === false) this.setState({ isExpanded: true });
    else this.setState({ isExpanded: false });
  };
  render() {
    const openNavStyle = {
      width: "300px"
    };
    const closeNavStyle = {
      width: "50px"
    };
    return (
      <React.Fragment>
        <div
          className="sideNav"
          style={this.state.isExpanded ? openNavStyle : closeNavStyle}
        >
          <Projects expand={this.expand} isExpanded={this.state.isExpanded} />
        </div>
      </React.Fragment>
    );
  }
}

export default Nav;
