import React, { Component } from "react";
import "../sidebar/sidebarStyle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStickyNote } from "@fortawesome/free-solid-svg-icons";

class Notes extends Component {
  state = {
    isExpanded: false,
    options: [
      {
        option: "View All",
        id: "000",
        func: this.props.loadNotes,
        isSolid: true
      },
      { option: "Write Note", id: "001", func: this.props.viewProject }
    ]
  };

  expand = e => {
    if (this.state.isExpanded === false) this.setState({ isExpanded: true });
    else this.setState({ isExpanded: false });
  };

  render() {
    const openDivStyle = {
      width: "100px",
      float: "left",
      overflow: "hidden"
    };
    const closeDivStyle = {
      width: "0px",
      display: "none"
    };
    return (
      <React.Fragment>
        <div className="option">
          <FontAwesomeIcon
            className="optionIcon"
            icon={faStickyNote}
            size="lg"
            onClick={this.expand}
          />
          <div
            className="expandOptions"
            style={this.state.isExpanded ? openDivStyle : closeDivStyle}
          >
            {this.state.options.map(x => (
              <p
                key={x.id}
                className="expandOption"
                datavalue={x.id}
                dataname={x.option}
                onMouseUp={() => this.setState({ isExpanded: false })}
                onClick={x.isSolid ? x.func : this.props.viewProject}
              >
                {x.option}
              </p>
            ))}
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Notes;
