import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCloud, faCloudMoon } from "@fortawesome/free-solid-svg-icons";
class Indicator extends Component {
  state = {};

  getAmPm = () => {
    let d = new Date();
    let amPm = d.getHours() >= 12 ? "pm" : "am";

    this.setState({
      amPm: amPm,
      description: this.props.description.description
    });
  };

  componentDidMount() {
    this.getAmPm();
  }

  render() {
    return (
      <div>
        <button onClick={console.log(this.state)}>test1</button>
        {this.state.amPm == "am" && <FontAwesomeIcon icon={faCloud} />}
        {this.state.amPm == "pm" && <FontAwesomeIcon icon={faCloudMoon} />}
      </div>
    );
  }
}

export default Indicator;
