import React, { Component } from "react";
import "../sidebar/sidebarStyle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faStickyNote,
  faChevronDown,
  faSearch
} from "@fortawesome/free-solid-svg-icons";

class CreateNote extends Component {
  state = {};

  componentDidMount() {
    document.addEventListener("mousedown", this.expand, false);
  }
  componentWillUnmount() {
    document.removeEventListener("mousedown", this.expand, false);
  }

  handleMinimize = e => {
    console.log("clicked out");
  };
  //rework this func to minimize when clicked out.
  minimize = e => {
    if (this.node.contains(e.target)) {
      console.log(e.target);
      return;
    } else this.handleMinimize();
  };

  render() {
    const textAreaStyle = {
      backgroundColor: "lightgoldenrodyellow",
      border: "none"
    };
    const textAreaStyle2 = {
      backgroundColor: "lightgoldenrodyellow",
      border: "none",
      width: "70%",
      borderBottom: "solid 1px lightgray"
    };
    const pDivStyle = {
      borderTop: "solid 1px lightgray",
      marginTop: "12px"
    };
    return (
      <React.Fragment>
        <div
          className="quickSelect"
          ref={node => (this.node = node)}
          onClick={this.minimize}
        >
          <h4>
            Add Note
            <span style={{ float: "right" }}>
              <button className="noteButton">-</button>
            </span>
          </h4>
          <textarea
            rows="4"
            cols="30"
            placeholder="write note..."
            style={textAreaStyle}
            onChange={this.props.handleChange}
          ></textarea>
          <div style={pDivStyle}>
            <p style={{ marginTop: "10px" }}>Add to Project</p>
          </div>
          <input
            type="text"
            placeholder="search..."
            style={textAreaStyle2}
            onChange={this.props.handleSearchInput}
          />
          <button className="noteButton" onClick={this.props.handleSearchOne}>
            <FontAwesomeIcon icon={faSearch} />
          </button>
          <button className="noteButton" onClick={this.props.handleSearchAll}>
            <FontAwesomeIcon icon={faChevronDown} />
          </button>
          <div style={{ float: "right", marginTop: "10px" }}>
            <button className="noteButton" onClick={this.props.handleCancel}>
              Cancel
            </button>
            <button className="noteButton" onClick={this.props.handleSubmit}>
              Submit
            </button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default CreateNote;
