import React, { Component } from "react";
import "../sidebar/sidebarStyle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faProjectDiagram } from "@fortawesome/free-solid-svg-icons";

class Projects extends Component {
  state = {
    options: [
      {
        option: "View All",
        func: this.props.handleProjects,
        isSolid: true
      },
      { option: "Project 1", id: "001", func: this.props.viewProject },
      { option: "Project 2", id: "002", func: this.props.viewProject },
      { option: "Project 3", id: "003", func: this.props.viewProject }
    ]
  };

  //need a componentdidmount once mongo is connected. all options except view all will be database saved

  render() {
    const openDivStyle = {
      width: "300px",
      backgroundColor: "green",
      opacity: ".5",
      paddingLeft: "80px",
      marginLeft: "50px",
      float: "left"
    };
    const closeDivStyle = {
      width: "0px",
      display: "none"
    };
    const iconStyle = {
      float: "left"
    };
    return (
      <React.Fragment>
        <div
          className="selectionClose"
          style={this.props.isExpanded ? iconStyle : "text-align:center"}
        >
          <FontAwesomeIcon
            icon={faProjectDiagram}
            size="lg"
            onClick={this.props.expand}
          />
        </div>
        <div style={this.props.isExpanded ? openDivStyle : closeDivStyle}>
          hello
        </div>
      </React.Fragment>

      // <div className="expanded">
      //   {this.state.options.map(x => (
      //     <p
      //       key={x}
      //       onClick={
      //         this.state.isSolid
      //           ? this.props.handleProjects
      //           : this.props.viewProject
      //       }
      //     >
      //       {x.option}
      //     </p>
      //   ))}
      //   <span className="tooltip">projects</span>
      // </div>
    );
  }
}

export default Projects;
