import React, { Component } from "react";
import "../sidebar/sidebarStyle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStickyNote } from "@fortawesome/free-solid-svg-icons";

class Notes extends Component {
  state = {
    isOpen: false,
    options: [
      {
        option: "View All",
        id: "000",
        func: this.props.loadNotes,
        isSolid: true
      },
      { option: "Write Note", id: "001", func: this.props.viewProject }
    ]
  };

  showOptions = e => {
    e.preventDefault();
    if (this.state.isOpen === false) this.setState({ isOpen: true });
    else this.setState({ isOpen: false });
  };

  render() {
    const openDivStyle = {
      width: "150px",
      paddingLeft: "20px",
      float: "left",
      overflow: "hidden"
    };
    const closeDivStyle = {
      width: "0px",
      display: "none"
    };
    const openIconStyle = {
      float: "left",
      marginLeft: "5%"
    };
    const closeIconStyle = {
      textAlign: "center"
    };
    return (
      <React.Fragment>
        <div
          className="selectionClose"
          style={this.props.isExpanded ? openIconStyle : closeIconStyle}
        >
          <FontAwesomeIcon
            icon={faStickyNote}
            size="lg"
            onClick={this.props.expandNav}
          />
        </div>
        <div style={this.state.isOpen ? openDivStyle : closeDivStyle}>
          {this.state.options.map(x => (
            <p
              key={x.id}
              className="expandOption"
              datavalue={x.id}
              dataname={x.option}
              onClick={x.isSolid ? x.func : this.props.viewProject}
            >
              {x.option}
            </p>
          ))}
        </div>
      </React.Fragment>
    );
  }
}

export default Notes;
