import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHamburger, faCalendar } from "@fortawesome/free-solid-svg-icons";
import "./sidebarStyle.css";
class Sidebar extends Component {
  state = {
    openClose: "close"
  };
  collapse = () => {
    if (this.state.openClose == "close") this.setState({ openClose: "open" });
    else this.setState({ openClose: "close" });
  };

  render() {
    const iconStyle = {
      size: "5px"
    };
    return (
      <div>
        {this.state.openClose == "close" && (
          <div className="sideNavClose">
            <p className="burger">
              <FontAwesomeIcon
                icon={faHamburger}
                size="lg"
                onClick={this.collapse}
              />
            </p>
            <p className="selection">
              <FontAwesomeIcon icon={faCalendar} size="lg" />
            </p>
          </div>
        )}
        {this.state.openClose == "open" && (
          <div className="sideNavOpen">
            <p className="burger">
              <FontAwesomeIcon
                icon={faHamburger}
                size="lg"
                onClick={this.collapse}
              />
            </p>
            <p className="selection">
              <FontAwesomeIcon icon={faCalendar} size="lg" />
            </p>
          </div>
        )}
      </div>
    );
  }
}

export default Sidebar;
